mapper.insert:
INSERT INTO user_info (
	user_code
    , user_name
    , user_birth
    , user_gender
    , user_tel
    , user_email
    , user_password
    , user_zipcode
    , user_address
    , user_detailaddress
    , resume_code
) VALUES (
	USER_CODE_SEQ.nextVal
    , #{userName}
    , #{userBirth}
    , #{userGender}
    , #{userTel}
    , #{userEmail}
    , #{userPassword}
    , #{userZipCode}
    , #{userAddress}
    , #{userDetailAddress}
    , 'N'

)

mapper.select:
SELECT
  *
FROM user_info
WHERE user_email = #{userEmail}
AND user_password = #{userPassword}
